<?phpnamespace UserBundle\Service\CsvService;use Doctrine\ORM\EntityManager;use JMS\DiExtraBundle\Annotation as DI;use UserBundle\Entity\User;use PortalBundle\Entity\Agency;use PortalBundle\Entity\Region;/** * @DI\Service("service.csv_import_users", public=true) */class UsersImportCsvService{    /**     * @var EntityManager     */    protected $em;    /**     * @var \Symfony\Component\HttpKernel\KernelInterface     */    protected $kernel;    /**     * @DI\InjectParams({     *     "em" = @DI\Inject("doctrine.orm.entity_manager"),     *     "kernel" = @DI\Inject("kernel")     * })     * @param $em     * @param  $kernel     */    public function __construct($em, $kernel)    {        $this->em = $em;        $this->kernel = $kernel;    }    /**     * @return string     */    public function getPath()    {        $path = realpath($this->kernel->getRootDir() . "/Resources/csv");        return $path;    }    /**     * parse a csv file and store it on db     * @param $filePath     */    public function importCsvUsers($filePath = null)    {        if ($filePath !== null) {            $csvFile = $filePath;        } else {            $csvFile = $this->getPath() . "/" . "utilisateurs.csv"; // Name of your CSV file        }        $header = true;        try {            if (($handle = fopen($csvFile, "r")) !== false) {                $counterLine = 0;                $fileNumberOfLine = -1;                $currentLine = 0;                $userByNni = null;                $userByEmail = null;                while (($fields = fgetcsv($handle, 4000, ";")) !== false && !feof($handle)) {                    $fileNumberOfLine++;                    if ($header) {                        $header = false;                        continue;                    }                    if (strlen(implode($fields)) != 0) {                        $user = new User();                        $agency = $this->em->getRepository('PortalBundle:Agency')->findOneByCode($fields[10]);                        $region = $this->em->getRepository('PortalBundle:Region')->findOneByCode($fields[9]);                        $userByGaia = $this->em->getRepository('UserBundle:User')->findOneByUsername($fields[2]);                        if (!empty($fields[7])) {                            $userByEmail = $this->em->getRepository('UserBundle:User')->findOneByEmail($fields[7]);                        }                        if (!empty($fields[4])) {                            $userByNni = $this->em->getRepository('UserBundle:User')->findOneByNni($fields[4]);                        }                        $currentLine++;                        if ($userByGaia === null &&  $userByEmail === null && $userByNni === null) {                            $user->setFirstName($fields[0]);                            $user->setLastName($fields[1]);                            $user->setUsername($fields[2]);                            $user->addRole($fields[3]);                            $user->setNni($fields[4]);                            $user->setPhone1($fields[5]);                            $user->setPhone2($fields[6]);                            $user->setEmail($fields[7]);                            $user->setEntity($fields[8]);                            $user->setAgency($agency);                            $user->setRegion($region);                            $user->setEnabled(true);                            $counterLine++;                            $this->em->persist($user);                        } else if (!empty($userByGaia)) {                            echo "Ligne ".$currentLine." ".$fields[0]." ".$fields[1]." "."n'a pas été ajouté car"." "."le gaia"." ".$fields[2]." "."existe déjà\n";                        } else if (!empty($userByEmail)) {                            echo "Ligne ".$currentLine." ".$fields[0]." ".$fields[1]." "."n'a pas été ajouté car"." "."le mail"." ".$fields[7]." "."existe déjà\n";                        } else if (!empty($userByNni)) {                            echo "Ligne ".$currentLine." ".$fields[0]." ".$fields[1]." "."n'a pas été ajouté car"." "."le NNI"." ".$fields[4]." "."existe déjà\n";                        }                    }                    $this->em->flush();                }                echo $counterLine ."/".$fileNumberOfLine." ". "ont été ajoutées avec succès\n";            }        } catch (\Exception $exception) {            echo $exception->getMessage();        }    }}